import React, {useState} from 'react';
import './App.css';
import InputToDo from "./components/InputToDo";
import AllItems from "./components/AllItems";
import {useDispatch, useSelector} from "react-redux";
import {RootState} from "../../state/store";
import {addItem, removeItem, setItemValue, toggleAllItems, toggleItem} from "../../slices/counterSlice";
import Footer from "./components/Footer";
import {TODO_TEMPLATE} from "./components/InputToDo/constants";


function App() {

    const [inputValue, setInputValue] = useState<string>("")

    const handleInput = (e: React.KeyboardEvent<HTMLDivElement>) => {
        if (e.key === 'Enter') {
            if (inputValue.trim() !== '') {
                const uniqueID = 'id' + Math.random().toString(16).slice(2);
                const newTodo = {
                    ...TODO_TEMPLATE,
                    value: inputValue,
                    id: uniqueID,
                    isCompleted: false,
                    isEditing: false,
                };
                dispatch(addItem(newTodo))
                setInputValue('')
            }
        }
    }

    const toggleItems = () => {
        dispatch(toggleAllItems())
    }

    const [filter, setFilter] = useState<boolean | null>(null)

    const [onChangeItem, setOnChangeItem] = useState<string | null>(null)

    const dispatch = useDispatch();

    const todos = useSelector((state: RootState) => {
        return state.todos.todos;
    });

    const deleteItem = (id: string) => {
        dispatch(removeItem(Array<string>(id)))
    }

    const toggleItemFulfillment = (id: string) => {
        dispatch(toggleItem(id))
    }

    const deleteCompletedItems = () => {
        // const itemsToDelete: Array<string> = todos.reduce((acc: Array<string>, item) => {
        //     item.isCompleted && acc.push(item.id);
        //     return acc;
        // }, []);
        const itemsToDelete: string[] = todos.reduce((acc: string[], item) => {
            return item.isCompleted ? [...acc, item.id] : acc;
        }, []);
        dispatch(removeItem(itemsToDelete))
    }

    const checkIsAllCompleted = () => {
        return !!(todos.filter((item) => !item.isCompleted)).length;
    }

    const itemsLeft = todos.filter((item) => !item.isCompleted).length

    const toggleEditProp = (id: string|null) => {
        setOnChangeItem(id)
    }

    const handleItemEditSave = (id: string, value: string) => {
        const payload = {
            id: id,
            value: value,
        }
        dispatch(setItemValue(payload))
    }

    // action - prefix on
    // action handler - prefix handle
    // onItemDelete={handleItemDelete}


    // todo move item creation to container (to this component)
    return (
        <div className="App">
            <h1>todos</h1>
            <div className="todo-part">
                <InputToDo
                    isAllCompleted={checkIsAllCompleted()}
                    handleTodoToggle={!!todos.length}
                    toggleItems={toggleItems}
                    handleInput={handleInput}
                    inputValue={inputValue}
                    setInputValue={setInputValue}
                />
                <AllItems
                    itemList={todos}
                    destroyItem={deleteItem}
                    checkItem={toggleItemFulfillment}
                    filter={filter}
                    toggleEditProp={toggleEditProp}
                    handleOnChangeItem={onChangeItem}
                    changeItem={handleItemEditSave}
                />
                {!!todos.length && (
                    <Footer
                        totalItems={todos.length}
                        itemLeft={itemsLeft}
                        filter={filter}
                        setFilter={setFilter}
                        destroyCompletedItems={deleteCompletedItems}
                    />
                )}
            </div>
        </div>
    );
}

export default App;
