import React, {useRef, useState} from "react";
import {Item} from "../../../../slices/counterSlice";
import './styles.scss'

interface IRemoveTodoTask {
    destroyItem: (id: string) => void
    checkItem: (id: string) => void
    toggleEditProp: (id: string) => void
    changeItem: (id: string, value: string) => void
    handleOnChangeItem: string|null
}

interface IAllItemsProps extends IRemoveTodoTask {
    itemList: Item[];
    filter: boolean | null
}

interface IItem extends IRemoveTodoTask {
    item: Item;

}

// TODO move to separate file
const ItemElement = ({item, destroyItem, checkItem, toggleEditProp, changeItem, handleOnChangeItem}: IItem) => {

    const inputRef = useRef(null)
    const [inputValue, setInputValue] = useState<string>(item.value)


    const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
        if (e.code === "Enter") {
            if (inputValue.trim() !== '') {
                changeItem(item.id, inputValue)
                editItem()
            }
        } else if (e.code === "Escape") {
            editItem()
            setInputValue(item.value)
        }
    }

    const handleBlur = () => {
        if (inputValue.trim() !== '') {
            changeItem(item.id, inputValue)
            editItem()
        }
    }

    const handleItemDelete = () => {
        destroyItem(item.id)
    }

    // todo whats going on here?
    const toggleItem = () => {
        !item.isEditing && checkItem(item.id)
    }

    // Todo rename this
    const editItem = () => toggleEditProp(item.id)

    return (
        <li className="todo-item" onDoubleClick={editItem}>
            <input id={"labelDone3" + item.id}
                   type="checkbox"
                   className="input-for-todo-list"
                   checked={item.isEditing ? false : item.isCompleted}
            />
            <label htmlFor={"labelDone3" + item.id}
                   className="label-for-todo-list"
                   onClick={toggleItem}
            />
            {item.id === handleOnChangeItem ? (
                <input
                    key={"input-edit"}
                    ref={inputRef}
                    type="text"
                    className="taskArea"
                    onKeyDown={handleKeyDown}
                    value={inputValue}
                    onChange={(e: React.ChangeEvent<HTMLInputElement>) => { // todo anon funk
                        setInputValue(e.target.value)
                    }}
                    onBlur={handleBlur}
                    autoFocus={true}
                />
            ) : (
                <>

                    <p>
                        {item.value}
                    </p>
                    <button type="button" className="deleteTaskButton" onClick={handleItemDelete}>
                        Ã—
                    </button>
                </>

            )}
        </li>
    )
}

// const FILTER_NAMES = {
//     all: "all",
//     completed: "completed",
// }
//
// const FILTERS = {
//     [FILTER_NAMES.all]: {
//         label: "All",
//         filterFunk: () => true,
//     },
//     [FILTER_NAMES.completed]: {
//         label: "Completed",
//         filterFunk: (todo) => todo.isCompleted,
//     },
//     [FILTER_NAMES.saga]: {
//         label: "Completed",
//         filterFunk: (todo) => todo.task === "saga",
//     }
// }
// Object.values(FILTERS).map(filter => (
//     <button onClick={() => setFilter(filter)}>
//         {filter.label}
//     </button>
// ))
const AllItems = ({itemList, destroyItem, checkItem, filter, toggleEditProp, changeItem, handleOnChangeItem}: IAllItemsProps) => {

    return (
        <ul className='all_todos'>
            {itemList
                .filter(item => filter === null || item.isCompleted === filter)
                .map(item => (
                    <ItemElement
                        key={item.id}
                        item={item}
                        destroyItem={destroyItem}
                        checkItem={checkItem}
                        toggleEditProp={toggleEditProp}
                        changeItem={changeItem}
                        handleOnChangeItem={handleOnChangeItem}
                    />
                ))}
        </ul>
    );
};
export default AllItems
