import React from "react";
import {Item} from "../../slices/counterSlice";
import './styles.scss'

interface HuynaEbana  {
    destroyItem: (id: string) => void
}

interface IAllItemsProps  extends HuynaEbana {
    itemList: Item[];
}

interface IItem extends  HuynaEbana{
    item: Item;
}

const ItemElement = ( {item, destroyItem} : IItem ) => {
    const handleItemDelete = () => {

            destroyItem(item.id)

    }

    return (
        <li>
            <input type="checkbox" className="inputForTodoList"/>
            <p>
                {item.value}
            </p>
            <button type="button" className="deleteTaskButton" onClick={handleItemDelete}>
                Ã—
            </button>
        </li>
    )
}

const AllItems = ({ itemList, destroyItem }: IAllItemsProps) => {
    return (
        <ul className='all_todos'>
            {itemList.map((item) => (
                <ItemElement
                    key={item.id}
                    item={item}
                    destroyItem={destroyItem}
                />
            ))}
        </ul>
    )
}
export default AllItems
