//         filterFunk: (todo) => todo.task === "saga",
//     }
// }
// Object.values(FILTERS).map(filter => (
//     <button onClick={() => setFilter(filter)}>
//         {filter.label}
//     </button>
// ))
const AllItems = ({
                      todos,
                      ...rest
                  }: IAllItemsProps) => {
    return (
        <ul className='all_todos'>
            {todos
                // .filter(item => filter === null || item.isCompleted === filter)
                .map(item => (
                    <ItemElement
                        key={item.id}
                        item={item}
                        {...rest}
                    />
                ))}
        </ul>
    );
};
export default AllItems
