import React, {useState} from "react";
import {Item} from "../../../../../slices/counterSlice";
import {IRemoveTodoTask} from "../index";


interface IItem extends IRemoveTodoTask {
    item: Item;
}

const ItemElement = ({ item, deleteItem, onToggleItemCompleteState, handleIdEditingItem,
                         changeItem, editingItemId }: IItem) => {


    const [inputValue, setInputValue] = useState<string>(item.value)

    const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
        if (e.code === "Enter") {
            if (inputValue.trim() !== '') {
                // onEditSubmit(item.id, inputValue)

                changeItem(item.id, inputValue)
                finishEditItem()

            }
        } else if (e.code === "Escape") {
            // onEditCancel()

            setInputValue(item.value)

            finishEditItem()

        }
    }

    const handleBlur = () => {
        if (inputValue.trim() !== '') {
            // handleEditSubmit()
            changeItem(item.id, inputValue)
            finishEditItem()
        }
    }

    const handleItemDelete = () => {
        deleteItem(item.id)
    }

    const handleToggleItemCompleteState = () => {
        !item.isEditing && onToggleItemCompleteState(item.id)
    }

    // handleItemEditStart handleItemEditSubmit handleItemEditCancel
    const startEditItem = () => handleIdEditingItem(item.id)

    const finishEditItem = () => handleIdEditingItem(null)

    // handleInputValueChange
    const controlInputValue = (e: React.ChangeEvent<HTMLInputElement>) => {
        setInputValue(e.target.value)
    }

    return (
        <li className="todo-item" onDoubleClick={startEditItem}>

            {item.id === editingItemId ? (
                <input
                    key={"input-edit"}
                    type="text"
                    className="taskArea"
                    onKeyDown={handleKeyDown}
                    value={inputValue}
                    onChange={controlInputValue}
                    onBlur={handleBlur}
                    autoFocus={true}
                />
            ) : (
                <>
                    <input id={"labelDone3" + item.id}
                           type="checkbox"
                           className="input-for-todo-list"
                           checked={item.isCompleted}
                    />
                    <label htmlFor={"labelDone3" + item.id}
                           className="label-for-todo-list"
                           onClick={handleToggleItemCompleteState}
                    />
                    <p>
                        {item.value}
                    </p>
                    <button type="button" className="deleteTaskButton" onClick={handleItemDelete}>
                        Ã—
                    </button>
                </>

            )}
        </li>
    )
}

export default ItemElement
