import React from "react";
import {Item} from "../../slices/counterSlice";
import './styles.scss'

interface IItems {
    itemList: Item[];
    setFilter: (filter: boolean | null) => void;
    destroyCompletedItems: () => void
}

const Footer = ({ itemList, setFilter, destroyCompletedItems } : IItems) => {
    const leftItems = () => {
        return itemList.filter((item) => !item.isCompleted).length
    }
    const displayAllItems = () => {
        setFilter(null)
    }
    const displayCompletedItems = () => {
        setFilter(true)
    }
    const displayActiveItems = () => {
        setFilter(false)
    }
    const removeAllCompleted = () => {
        destroyCompletedItems()
    }

    return (
        <footer className="todo-footer">
            <span className="todo-count">
                {leftItems() === 1 ? "1 item left" : `${leftItems()} items left`}
            </span>
            <button onClick={displayAllItems}>all</button>
            <button onClick={displayCompletedItems}>Completed</button>
            <button onClick={displayActiveItems}>Active</button>
            <button onClick={removeAllCompleted}>Remove all completed</button>
            {/*<ul className="filters">*/}
            {/*    {labels.map(filter =>(*/}
            {/*        <li key={filter.label}>*/}
            {/*            <div*/}
            {/*                onClick={() => setSelectedFilter(filter)}*/}
            {/*                className={selectedFilter.label === filter.label ? "selectedFilter" : ""}*/}
            {/*            >*/}
            {/*                {filter.label}*/}
            {/*            </div>*/}
            {/*        </li>*/}
            {/*    ))}*/}
            {/*</ul>*/}
            {/*<button onClick={clearCompleted} className="clearCompleted">*/}
            {/*    Clear Completed*/}
            {/*</button>*/}
        </footer>
    )
}
export default Footer
