import React from 'react';
import './styles.scss'

// @ts-ignore

//

interface ToDoInputName {
    isAllCompleted: boolean;
    handleTodoToggle: boolean;
    toggleItems: () => void;
    handleInput: (e: React.KeyboardEvent<HTMLDivElement>) => void;
    inputValue: string;
    setInputValue: (inputValue: string) => void;
}

function InputToDo({
                       isAllCompleted, handleTodoToggle, toggleItems,
                       handleInput, inputValue, setInputValue
                   }: ToDoInputName) {

    const onInputValue = (e: React.ChangeEvent<HTMLInputElement>) => {
        setInputValue(e.target.value)
    }

    return (
        <div className='items'>

            <button
                className={`items-toggle-all ${isAllCompleted ? '' : 'checked'}`}
                onClick={toggleItems}
            >
                <span
                    style={handleTodoToggle ? {} : {opacity: 0}}
                >
                    â€º
                </span>
            </button>
            <input
                type='text'
                className='items-input'
                placeholder='What needs to be done?'
                onKeyDown={handleInput}
                value={inputValue}
                onChange={onInputValue}
            />
        </div>
    );
}

export default InputToDo;
