import React from "react";
import {Item} from "../../../../slices/counterSlice";
import './styles.scss'
import ItemElement from "./components";


export interface IRemoveTodoTask {
    deleteItem: (id: string) => void
    onToggleItemCompleteState: (id: string) => void
    handleIdEditingItem: (id: string|null) => void
    changeItem: (id: string, value: string) => void
    editingItemId: string|null
}

interface IAllItemsProps extends IRemoveTodoTask {
    itemList: Item[];
    filter: boolean | null
}

// const FILTER_NAMES = {
//     all: "all",
//     completed: "completed",
// }
//
// const FILTERS = {
//     [FILTER_NAMES.all]: {
//         label: "All",
//         filterFunk: () => true,
//     },
//     [FILTER_NAMES.completed]: {
//         label: "Completed",
//         filterFunk: (todo) => todo.isCompleted,
//     },
//     [FILTER_NAMES.saga]: {
//         label: "Completed",
//         filterFunk: (todo) => todo.task === "saga",
//     }
// }
// Object.values(FILTERS).map(filter => (
//     <button onClick={() => setFilter(filter)}>
//         {filter.label}
//     </button>
// ))
const AllItems = ({itemList, deleteItem, onToggleItemCompleteState, filter,
                      handleIdEditingItem, changeItem, editingItemId}: IAllItemsProps) => {
    return (
        <ul className='all_todos'>
            {itemList
                .filter(item => filter === null || item.isCompleted === filter)
                .map(item => (
                    <ItemElement
                        key={item.id}
                        item={item}
                        deleteItem={deleteItem}
                        onToggleItemCompleteState={onToggleItemCompleteState}
                        handleIdEditingItem={handleIdEditingItem}
                        changeItem={changeItem}
                        editingItemId={editingItemId}
                    />
                ))}
        </ul>
    );
};
export default AllItems
