import React from "react";
import {Item} from "../../slices/counterSlice";

interface IItems {
    itemList: Item[];
}

const Footer = ({ itemList } : IItems) => {
    const leftItems = () => {
        return itemList.filter((item) => !item.isCompleted).length
    }
    
    return (
        <footer className="todoFooter">
            <span className="todoCount">
                {leftItems() === 1 ? "1 item left" : `${leftItems()} items left`}
            </span>
            <ul className="filters">
                {labels.map(filter =>(
                    <li key={filter.label}>
                        <div
                            onClick={() => setSelectedFilter(filter)}
                            className={selectedFilter.label === filter.label ? "selectedFilter" : ""}
                        >
                            {filter.label}
                        </div>
                    </li>
                ))}
            </ul>
            <button onClick={clearCompleted} className="clearCompleted">
                Clear Completed
            </button>
        </footer>
    )
}
export default Footer
