import React, {useRef, useState} from "react";
import {Item} from "../../slices/counterSlice";
import './styles.scss'

interface IRemoveTodoTask  {
    destroyItem: (id: string) => void
    checkItem: (id: string) => void
    toggleEditProp: (id: string) => void
    changeItem: (id: string) => void
}

interface IAllItemsProps  extends IRemoveTodoTask {
    itemList: Item[];
    filter: boolean|null
}

interface IItem extends  IRemoveTodoTask{
    item: Item;

}

const ItemElement = ( { item, destroyItem, checkItem, toggleEditProp, changeItem } : IItem ) => {

    const inputRef = useRef(null)
    const [inputValue, setInputValue] = useState<string>("")


    // const handleTodoEditSave = (newText: string) => {
    //     updateTodoDataById(item.id, {
    //         task: newText,
    //         isEditing: false,
    //     })
    // }

    const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
        console.log(e.key)
        if (e.code === "Enter") {
            if (inputValue.trim() !== '') {
                changeItem("bla bla bla")
            }
        } else if (e.code === "Escape") {
            // handleTodoEditCancel(todo.id);
        }
    }

    const handleItemDelete = () => {
        destroyItem(item.id)
    }

    const toggleItem = () => {
        checkItem(item.id)
    }

    const editItem = () => toggleEditProp(item.id)

    return (
        <li className="todo-item">
            {item.isEditing ? (
                <input
                    key={"input-edit"}
                    ref={inputRef}
                    type="text"
                    className="taskArea"
                    onKeyDown={handleKeyDown}
                    value={inputValue}
                    onChange={(e: React.ChangeEvent<HTMLInputElement>) => {setInputValue(e.target.value)}}
                    // onBlur={handleBlur}
                /> )
            : (
                <>
                    <input id={"labelDone3" + item.id} type="checkbox" className="input-for-todo-list" checked={item.isCompleted}/>
                    <label htmlFor={"labelDone3" + item.id} className="label-for-todo-list" onClick={toggleItem} />

                    <p onDoubleClick={editItem}>
                        {item.value}
                    </p>
                    <button type="button" className="deleteTaskButton" onClick={handleItemDelete}>
                        Ã—
                    </button>
                </>
                )}

        </li>
    )
}

// const FILTER_NAMES = {
//     all: "all",
//     completed: "completed",
// }
//
// const FILTERS = {
//     [FILTER_NAMES.all]: {
//         label: "All",
//         filterFunk: () => true,
//     },
//     [FILTER_NAMES.completed]: {
//         label: "Completed",
//         filterFunk: (todo) => todo.isCompleted,
//     },
//     [FILTER_NAMES.saga]: {
//         label: "Completed",
//         filterFunk: (todo) => todo.task === "saga",
//     }
// }
// Object.values(FILTERS).map(filter => (
//     <button onClick={() => setFilter(filter)}>
//         {filter.label}
//     </button>
// ))
const AllItems = ({ itemList, destroyItem, checkItem, filter, toggleEditProp, changeItem }: IAllItemsProps) => {
    console.log(itemList, itemList
        .filter(item => filter === null || item.isCompleted === filter))
    return (
        <ul className='all_todos'>
            {itemList
                .filter(item => filter === null || item.isCompleted === filter)
                .map(item => (
                    <ItemElement
                        key={item.id}
                        item={item}
                        destroyItem={destroyItem}
                        checkItem={checkItem}
                        toggleEditProp={toggleEditProp}
                        changeItem={changeItem}
                    />
                ))}
        </ul>
    );
};
export default AllItems
