import React, {useRef} from "react";
import {Item} from "../../slices/counterSlice";
import './styles.scss'

interface IRemoveTodoTask  {
    destroyItem: (id: string) => void
    checkItem: (id: string) => void
    toggleEditProp: (id: string) => void
}

interface IAllItemsProps  extends IRemoveTodoTask {
    itemList: Item[];
    filter: boolean|null
}

interface IItem extends  IRemoveTodoTask{
    item: Item;

}

const ItemElement = ( { item, destroyItem, checkItem, toggleEditProp } : IItem ) => {

    const inputRef = useRef(null)

    const handleTodoEditSave = (id, newText) => {
        updateTodoDataById(id, {
            task: newText,
            isEditing: false,
        })
    }

    const handleKeyDown = (e) => {
        if (e.code === "Enter") {
            handleTodoEditSave(todo.id, inputRef.current.value)
        } else if (e.code === "Escape") {
            handleTodoEditCancel(todo.id);
        }
    }

    const handleItemDelete = () => {
        destroyItem(item.id)
    }

    const toggleItem = () => {
        checkItem(item.id)
    }

    const editItem = () => {
        console.log('CHEEEEEEEEEEEEEEEEEEEEEEE')
        toggleEditProp(item.id)
    }

    return (
        <li className="todo-item">
            {item.isEditing ?(
                <input
                    key={"input-edit"}
                    ref={inputRef}
                    type="text"
                    className="taskArea"
                    onKeyDown={handleKeyDown}
                    // onBlur={handleBlur}
                /> )
            : (
                <>
                    <input id={"labelDone3" + item.id} type="checkbox" className="input-for-todo-list" checked={item.isCompleted}/>
                    <label htmlFor={"labelDone3" + item.id} className="label-for-todo-list" onClick={toggleItem} />

                    <p onDoubleClick={(e: React.ChangeEvent<HTMLInputElement>) => toggleEditProp}>
                        {item.value}
                    </p>
                    <button type="button" className="deleteTaskButton" onClick={handleItemDelete}>
                        Ã—
                    </button>
                </>
                )}

        </li>
    )
}

