// ))
const AllItems = ({ itemList, destroyItem, checkItem, filter, toggleEditProp }: IAllItemsProps) => {
    console.log(itemList)
    return (
        <ul className='all_todos'>
            {itemList
                .filter(item => filter === null || item.isCompleted === filter)
                .map(item => (
                    <ItemElement
                        key={item.id}
                        item={item}
                        destroyItem={destroyItem}
                        checkItem={checkItem}
                        toggleEditProp={toggleEditProp}
                    />
                ))}
        </ul>
    );
};
export default AllItems
