import { createSlice } from "@reduxjs/toolkit";

export interface Item {
    value: string;
    id: string;
    isCompleted: boolean;
    isEditing: boolean;
}

const initialState: { todos: Item[] } = {
    todos: [],
}


const todos = createSlice({
    name: "todoActions",
    initialState,
    reducers: {
        addItem: (state, action) => {
            state.todos = [...state.todos, action.payload]
        },
        removeItem: (state, action) => {
            state.todos = [...state.todos.filter((item) => item.id !== action.payload)]
        },
        toggleItem: (state, action) => {
            state.todos = state.todos.map((item) =>
                item.id === action.payload ? { ...item, isCompleted: !item.isCompleted } : item
            );
        },
        toggleAllItems: (state) => {
            const isAllCompleted = !!(state.todos.filter((item) => !item.isCompleted)).length;
            state.todos = state.todos.map(item => ({...item, isCompleted: isAllCompleted}))
        },
        toggleEditItem: (state, action) => {
            state.todos = state.todos.map((item) =>
                item.id === action.payload ? { ...item, isEditing: !item.isEditing } : item
            );
        },
        setItemValue: (state, action) => {
            // state.todos = state.todos.map((item) =>
            //     item.id === action.payload ? { ...item, isEditing: !item.isEditing } : item
            // );
            console.log(action, "sahashdsadhsadhsadhsadhasdhasdh")
        }
    },

});

const { reducer } = todos;

export const { addItem, removeItem, toggleItem, toggleAllItems, toggleEditItem , setItemValue} = todos.actions
export default reducer;
