import React from "react";
import {Item} from "../../slices/counterSlice";
import './styles.scss'

interface IRemoveTodoTask  {
    destroyItem: (id: string) => void
    checkItem: (id: string) => void
}

interface IAllItemsProps  extends IRemoveTodoTask {
    itemList: Item[];
}

interface IItem extends  IRemoveTodoTask{
    item: Item;

}

const ItemElement = ( { item, destroyItem, checkItem } : IItem ) => {
    const handleItemDelete = () => {
        destroyItem(item.id)
    }

    const toggleItem = () => {
        checkItem(item.id)
    }

    return (
        <li className="todo-item">
            <label htmlFor={"labelDone3" + item.id} className="label-for-todo-list" onClick={toggleItem} >
                <input type="checkbox" className="input-for-todo-list" {...{checked: item.isCompleted}}/>
            </label>
            <p>
                {item.value}
            </p>
            <button type="button" className="deleteTaskButton" onClick={handleItemDelete}>
                Ã—
            </button>
        </li>
    )
}

const AllItems = ({ itemList, destroyItem, checkItem }: IAllItemsProps) => {
    console.log(itemList)
    return (
        <ul className='all_todos'>
            {itemList.map((item) => (
                <ItemElement
                    key={item.id}
                    item={item}
                    destroyItem={destroyItem}
                    checkItem={checkItem}
                />
            ))}
        </ul>
    )
}
export default AllItems
