import React, {useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
// import { AppState, Item} from "../../state/types";
// import { ActionType } from "../../state/actions";
import './styles.scss'

// @ts-ignore

//


import { TODO_TEMPLATE } from './constants'
import {addItem} from "../../slices/counterSlice";

interface ToDoInputName {
    name: string;
}
function InputToDo ({name}: ToDoInputName ) {
    const dispatch = useDispatch();
    // const selectedInputValue = useSelector((state: AppState) => state.inputValue);
    // const todos = useSelector((state: AppState) => state.todos);
    const [inputValue, setInputValue] = useState('')

    // const handleTodoCreate = (text: string) => {
    //     if (text.trim() === "") return;
    //     const uniqueID = "id" + Math.random().toString(16).slice(2);
    //     const newTodo = {...TODO_TEMPLATE, task: text, id: uniqueID, isChecked: false}
    //
    // }
    const handleInput = (e: React.KeyboardEvent<HTMLDivElement>) => {
        if (e.key === 'Enter') {
            if (inputValue.trim() !== '') {
                const uniqueID = 'id' + Math.random().toString(16).slice(2);
                dispatch(addItem(
                    {...TODO_TEMPLATE.task = inputValue}
                ))
            }
            // const newTodo: Item = { ...TODO_TEMPLATE, todoValue: selectedInputValue, id: uniqueID, isChecked: false };
            // dispatch({ type: ActionType.AddItem, todo: newTodo });
        }
    }
    // console.log(todos)

    return (
        <div className='items'>
            <button className='items-toggle-all'>
                <span>
                    â€º
                </span>
            </button>
            <input type='text'
                   className='items-input'
                   placeholder={name}
                   onKeyDown={handleInput}
                   value={inputValue}
                   // onChange={(e: React.ChangeEvent<HTMLInputElement>) => {
                   //     dispatch({ type: ActionType.AddItem, value: e.target.value });
                   // }}
                   onChange={(e: React.ChangeEvent<HTMLInputElement>) => {setInputValue(e.target.value)}}
            />
        </div>
    );
}
export default InputToDo;
