import React, {useState} from "react";
import {Item} from "../../../../../slices/counterSlice";
import {IRemoveTodoTask} from "../index";


interface IItem extends IRemoveTodoTask {
    item: Item;
}

const ItemElement = ({item, deleteItem, checkItem, toggleEditProp, changeItem, handleChangeItem}: IItem) => {


    const [inputValue, setInputValue] = useState<string>(item.value)

    const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
        if (e.code === "Enter") {
            if (inputValue.trim() !== '') {
                changeItem(item.id, inputValue)
                finishEditItem()

            }
        } else if (e.code === "Escape") {
            setInputValue(item.value)
            finishEditItem()

        }
    }

    const handleBlur = () => {
        if (inputValue.trim() !== '') {
            changeItem(item.id, inputValue)
            finishEditItem()
        }
    }

    const handleItemDelete = () => {
        deleteItem(item.id)
    }

    const toggleItemFulfillment = () => {
        !item.isEditing && checkItem(item.id)
    }

    const startEditItem = () => toggleEditProp(item.id)

    const finishEditItem = () => toggleEditProp(null)

    const controlInputValue = (e: React.ChangeEvent<HTMLInputElement>) => {
        setInputValue(e.target.value)
    }

    return (
        <li className="todo-item" onDoubleClick={startEditItem}>
            <input id={"labelDone3" + item.id}
                   type="checkbox"
                   className="input-for-todo-list"
                   checked={item.id === handleChangeItem ? false : item.isCompleted}
            />
            <label htmlFor={"labelDone3" + item.id}
                   className="label-for-todo-list"
                   onClick={toggleItemFulfillment}
            />
            {item.id === handleChangeItem ? (
                <input
                    key={"input-edit"}
                    type="text"
                    className="taskArea"
                    onKeyDown={handleKeyDown}
                    value={inputValue}
                    onChange={controlInputValue}
                    onBlur={handleBlur}
                    autoFocus={true}
                />
            ) : (
                <>
                    <p>
                        {item.value}
                    </p>
                    <button type="button" className="deleteTaskButton" onClick={handleItemDelete}>
                        Ã—
                    </button>
                </>

            )}
        </li>
    )
}

export default ItemElement
