import React from "react";
import {Item} from "../../slices/counterSlice";
import './styles.scss'

interface IItems {
    itemList: Item[];
    setFilter: (filter: boolean | null) => void;
    destroyCompletedItems: () => void
}

const Footer = ({ itemList, setFilter, destroyCompletedItems } : IItems) => {
    const leftItems = () => {
        return itemList.filter((item) => !item.isCompleted).length
    }
    const displayAllItems = () => {
        setFilter(null)
    }
    const displayCompletedItems = () => {
        setFilter(true)
    }
    const displayActiveItems = () => {
        setFilter(false)
    }
    const removeAllCompleted = () => {
        destroyCompletedItems()
    }

    const footerButtons = [
        {
            action: displayAllItems,
            label: "All",
            classname: "filter",
        },
        {
            action: displayActiveItems,
            label: "Active",
            classname: "filter",
        },
        {
            action: displayCompletedItems,
            label: "Completed",
            classname: "filter",
        },
        {
            action: removeAllCompleted,
            label: "Clear completed",
            classname: "action"
        },
    ]

    return (
        <footer className="todo-footer">
            <span className="todo-count">
                {leftItems() === 1 ? "1 item left" : `${leftItems()} items left`}
            </span>
            {
                footerButtons.map((item) => (
                    <button onClick={() => {
                        item.classname = "active_filter"
                        return (item.action)
                    }} className={item.classname}>{item.label}</button>)
                )
            }

        </footer>
    )
}
export default Footer
