import React, {useState} from 'react';
import './App.css';
import InputToDo from "./components/InputToDo";
import AllItems from "./components/AllItems";
import {useDispatch, useSelector} from "react-redux";
import {RootState} from "../../state/store";
import {removeItem, setItemValue, toggleEditItem, toggleItem} from "../../slices/counterSlice";
import Footer from "./components/Footer";

interface AppName {
    name: string;
}

// todo clean up shit

function App({name}: AppName) {

    const [filter, setFilter] = useState<boolean|null>(null)

    const dispatch = useDispatch();

    const todos = useSelector((state: RootState) => {
        return state.counter.todos;
    });

    const destroyItem = (id: string) => {
        dispatch(removeItem(id))
    }

    // todo completed
    const checkItem = (id: string) => {
        dispatch(toggleItem(id))
    }

    // todo remove | delete
    const destroyCompletedItems = () => {
        // todo make reducer for removing multiple items at once or set array of items
        todos.map((item) => item.isCompleted && dispatch(removeItem(item.id)))
    }

    // todo checkIsAllCompleted
    const isAllCompleted = () => {
        return !!(todos.filter((item) => !item.isCompleted)).length;
    }

    // todo itemsLeft || itemsLeftCount
    const leftItems = todos.filter((item) => !item.isCompleted).length

    // todo move isEditing to editingItemId useState. remove isEditing property from todo item
    const toggleEditProp = (id: string) => {
        dispatch(toggleEditItem(id))
    }

    // todo handleItemEditSave

    const changeItem = (id: string, value: string) => {
        const payload = {
            id: id,
            value: value,
        }
        dispatch(setItemValue(payload))
    }

    // action - prefix on
    // action handler - prefix handle
    // onItemDelete={handleItemDelete}


    // todo move item creation to container (to this component)
    return (
        <div className="App">
            <h1>{name}</h1>
            <InputToDo
                name='What needs to be done?'
                isAllCompleted={isAllCompleted()}
                handleTodoToggle={!!todos.length}
            />
            <AllItems
                itemList={todos}
                destroyItem={destroyItem}
                checkItem={checkItem}
                filter={filter}
                toggleEditProp={toggleEditProp}
                changeItem={changeItem}
            />
            {!!todos.length && (
                <Footer
                    itemLeft={leftItems}
                    filter={filter}
                    setFilter={setFilter}
                    destroyCompletedItems={destroyCompletedItems}
                />
            )}
        </div>
    );
}

export default App;
