import React from "react";
import {Item} from "../../slices/counterSlice";
import './styles.scss'

interface IItems {
    itemList: Item[];
    setFilter: (filter: boolean | null) => void;
    destroyCompletedItems: () => void;
    filter: boolean|null;
}
const Footer = ({ itemList, setFilter, destroyCompletedItems, filter } : IItems) => {
    const leftItems = () => {
        return itemList.filter((item) => !item.isCompleted).length
    }
    const displayAllItems = () => {
        setFilter(null)
    }
    const displayCompletedItems = () => {
        setFilter(true)
    }
    const displayActiveItems = () => {
        setFilter(false)
    }
    const removeAllCompleted = () => {
        destroyCompletedItems()
    }

    const footerButtons = [
        {
            action: displayAllItems,
            label: "All",
            classname: "filter",
        },
        {
            action: displayActiveItems,
            label: "Active",
            classname: "filter",
        },
        {
            action: displayCompletedItems,
            label: "Completed",
            classname: "filter",
        },
    ]
    const FILTER_NAMES = {
    All: "All",
    true: "Active",
    false: "Completed",
    }

    const setClassName = () => {
        filter === null ? (
            footerButtons.map((item) =>
                item.label === FILTER_NAMES.All ? item.classname = "active_filter" : "filter"
            )
        ) : (
            footerButtons.map((item) =>
                item.label === FILTER_NAMES[`${filter}`] ? item.classname = "active_filter" : "filter"
            )
        )
    }

    return (
        <footer className="todo-footer">
            <span className="todo-count">
                {leftItems() === 1 ? "1 item left" : `${leftItems()} items left`}
            </span>
            <div>
                {
                    footerButtons.map((item) => (
                        <button onClick={item.action} className={
                            item.classname
                        }>{item.label}</button>)
                    )
                }
            </div>
            <button onClick={removeAllCompleted} className="action">
                Clear completed
            </button>
        </footer>
    )
}
export default Footer
