import React from "react";
import './styles.scss'

interface IItems {
    totalItems: number;
    itemLeft: number;
    setFilter: (filter: boolean | null) => void;
    onDeleteCompletedItems: () => void;
    filter: boolean|null;
}
const Footer = ({ totalItems, itemLeft, setFilter, onDeleteCompletedItems, filter } : IItems) => {

    const displayAllItems = () => {
        setFilter(null)
    }
    const displayCompletedItems = () => {
        setFilter(true)
    }
    const displayActiveItems = () => {
        setFilter(false)
    }

    const FILTER_NAMES = {
        null: "All",
        false: "Active",
        true: "Completed",
    }

    const footerButtons = [
        {
            action: displayAllItems,
            label: FILTER_NAMES.null,
        },
        {
            action: displayActiveItems,
            label: FILTER_NAMES.false,
        },
        {
            action: displayCompletedItems,
            label: FILTER_NAMES.true,
        },
    ]

    const handleDeleteAllCompletedItems = () => {
        onDeleteCompletedItems()
    }

    return (
        <footer className="todo-footer">
            <span className="todo-count">
                {itemLeft === 1 ? "1 item left" : `${itemLeft} items left`}
            </span>
            <div>
                {
                    footerButtons.map((item) => (
                        <button
                            onClick={item.action}
                            className={
                                item.label === FILTER_NAMES[`${filter}`] ? "active_filter"
                                     : "filter"
                            }
                        >
                            {item.label}
                        </button>
                        )
                    )
                }
            </div>
            <button
                onClick={handleDeleteAllCompletedItems}
                className={ itemLeft < totalItems ? "" : "action hidden"}
            >
                Clear completed
            </button>

        </footer>
    )
}
export default Footer
