import React, {useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { AppState, Item} from "../../state/types";
import { ActionType } from "../../state/actions";
import './styles.scss'

// @ts-ignore

//


import { TODO_TEMPLATE } from './constants'

interface ToDoInputName {
    name: string;
}
function InputToDo ({name}: ToDoInputName ) {
    const [inputValue, setInputValue] = useState<string>('')
    const [todos, setTodos] = useState<Array<Object>>([]);

    const handleTodoCreate = (text: string) => {
        if (text.trim() === "") return;
        const uniqueID = "id" + Math.random().toString(16).slice(2);
        const newTodo = {...TODO_TEMPLATE, task: text, id: uniqueID, isChecked: false}
        setTodos([...todos, newTodo])

    }
    const handleInput = (e: React.KeyboardEvent<HTMLDivElement>) => {
        if (e.key === "Enter") {
            handleTodoCreate(inputValue)
            setInputValue('')
        }
    }

    return (
        <div className='items'>
            <button className='items-toggle-all'>
                <span>
                    â€º
                </span>
            </button>
            <input type='text'
                   className='items-input'
                   placeholder={name}
                   onKeyDown={handleInput}
                   value={inputValue}
                   onChange={(e: React.ChangeEvent<HTMLInputElement>) => {setInputValue(e.target.value)}}
            />
        </div>
    );
}
export default InputToDo;
