import React, {useState} from 'react';
import { useDispatch } from 'react-redux';
import {toggleAllItems} from "../../slices/counterSlice";
import './styles.scss'

// @ts-ignore

//


import { TODO_TEMPLATE } from './constants'
import {addItem} from "../../slices/counterSlice";

interface ToDoInputName {
    name: string;
}
function InputToDo ({name}: ToDoInputName ) {
    const dispatch = useDispatch();
    const [inputValue, setInputValue] = useState("")

    const handleInput = (e: React.KeyboardEvent<HTMLDivElement>) => {
        if (e.key === 'Enter') {
            if (inputValue.trim() !== '') {
                const uniqueID = 'id' + Math.random().toString(16).slice(2);
                const newTodo = {
                    ...TODO_TEMPLATE,
                    value: inputValue,
                    id: uniqueID,
                    isCompleted: false,
                    isEditing: false,
                };
                dispatch(addItem(newTodo))
                setInputValue('')
            }
        }
    }

    const toggleItems = () => {
        dispatch(toggleAllItems())
    }

    return (
        <div className='items'>
            <button className='items-toggle-all' onClick={toggleItems}>
                <span>
                    â€º
                </span>
            </button>
            <input type='text'
                   className='items-input'
                   placeholder={name}
                   onKeyDown={handleInput}
                   value={inputValue}
                   onChange={(e: React.ChangeEvent<HTMLInputElement>) => {setInputValue(e.target.value)}}
            />
        </div>
    );
}
export default InputToDo;
